document.getElementById("message").addEventListener("submit",async function(e){e.preventDefault();let t=document.getElementById("name").value,r=document.getElementById("email").value,a=document.getElementById("textarea").value;try{let i=await fetch("http://localhost:3000/register",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({name:t,email:r,textarea:a})});if(i.ok)alert("Registration successful!");else{let l=await i.text();alert("Failed to register: "+l)}}catch(n){console.error("Error:",n),alert("Error occurred while submitting the form.")}});